    .m_axi_awprot_o       (m_axi_awprot_o       ),
    .m_axi_awqos_o        (m_axi_awqos_o        ),
    .m_axi_awregion_o     (m_axi_awregion_o     ),
    .m_axi_awatop_o       (m_axi_awatop_o       ),
    .m_axi_awuser_o       (m_axi_awuser_o       ),
    
        .m_axi_wready_i       (m_axi_wready_i       ),
